'/*-------------------------------------',
' * APEX Confirm functions',
' * Version: 1.0 (25.12.2017)',
' * Author:  Alli Pierre Yotti',
' *-------------------------------------',
'*/',
'FUNCTION render_confirm(p_dynamic_action IN apex_plugin.t_dynamic_action,',
'                        p_plugin         IN apex_plugin.t_plugin)',
'  RETURN apex_plugin.t_dynamic_action_render_result IS',
'  --',
'  -- plugin attributes',
'  l_result       apex_plugin.t_dynamic_action_render_result;',
'  l_theme        VARCHAR2(100) := p_dynamic_action.attribute_01;',
'  l_column_class  VARCHAR2(100) := p_dynamic_action.attribute_11;',
'  l_title VARCHAR2(1000) := p_dynamic_action.attribute_02;',
'  l_content_item VARCHAR2(200) := p_dynamic_action.attribute_12;',
'  l_close_icon VARCHAR2(50) := p_dynamic_action.attribute_03;',
'  l_animation    VARCHAR2(100) := p_dynamic_action.attribute_04;',
'  l_action      clob := p_dynamic_action.attribute_13;',
'  l_action_label      VARCHAR2(50) := p_dynamic_action.attribute_14;',
'  l_dialog_content      clob := p_dynamic_action.attribute_15;',
'  -- js/css file vars',
'  l_confirme_js  VARCHAR2(50);',
' ',
'  l_confirm_js  VARCHAR2(50);',
'  l_confirm_css VARCHAR2(50);',
'  l_custom_css VARCHAR2(50);',
'  --',
'BEGIN',
'  -- attribute defaults',
'  l_theme        := nvl(l_theme,',
'                        ''blue'');',
'  l_column_class  := nvl(l_column_class,',
'                        ''TEXT'');',
'  l_close_icon := nvl(l_close_icon,',
'                        ''false'');',
'  l_animation    := nvl(l_animation,',
'                        ''fade'');',
'  l_action      := nvl(l_action,',
'                        ''alert(1)'');',
'  l_dialog_content      := nvl(l_dialog_content,',
'                        ''your content here'');',
'                        ',
'  -- Debug',
'  IF apex_application.g_debug THEN',
'    apex_plugin_util.debug_dynamic_action(p_plugin         => p_plugin,',
'                                          p_dynamic_action => p_dynamic_action);',
'    -- set js/css filenames (normal version)',
'    l_confirm_js  := ''jquery-confirm.min'';',
'    l_confirme_js  := ''apexconfirm'';',
'    l_confirm_css := ''jquery-confirm.min'';',
'    l_custom_css := ''custom'';',
'    ',
'  ELSE',
'    -- minified version',
'    l_confirm_js  := ''jquery-confirm.min'';',
'    l_confirme_js  := ''apexconfirm.min'';',
'    l_confirm_css := ''jquery-confirm.min'';',
'    l_custom_css := ''custom'';',
'     ',
'  END IF;',
'  --',
'  -- add jqueryconfirm and apexconfirm js files',
'  ',
'   apex_javascript.add_library(p_name           => l_confirm_js,',
'                              p_directory      => p_plugin.file_prefix ||',
'                                                  ''js/'',',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'                              ',
'  ',
'  --',
'  apex_javascript.add_library(p_name           => l_confirme_js,',
'                              p_directory      => p_plugin.file_prefix ||',
'                                                  ''js/'',',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'  --',
'  -- add confirm css and theme css files',
'  apex_css.add_file(p_name      => l_confirm_css,',
'                    p_directory => p_plugin.file_prefix || ''css/'');',
'                    ',
' -- add custom css',
'  apex_css.add_file(p_name      => l_custom_css,',
'                    p_directory => p_plugin.file_prefix || ''css/'');',
'  ',
'  --',
'  --',
'  l_result.javascript_function := ''apexConfirm.showConfirm'';--(''||p_dynamic_action.init_javascript_code||'')'';',
'  l_result.attribute_01        := l_theme;',
'  l_result.attribute_02        := l_title;',
'  l_result.attribute_03        := l_close_icon;',
'  l_result.attribute_04        := l_animation;',
'  l_result.attribute_11        := l_column_class;',
'  l_result.attribute_12        := l_content_item;',
'  l_result.attribute_13        := l_action;',
'  l_result.attribute_14        := l_action_label;',
'  l_result.attribute_15        := l_dialog_content;',
'  ',
'  --',
'  RETURN l_result;',
'  --',
'END render_confirm;'))
,p_api_version=>1
,p_render_function=>'render_confirm'
,p_standard_attributes=>'ITEM:BUTTON:REGION:JQUERY_SELECTOR'
